import React from "react";
import { useState } from "react";
import { Container, Row, Col } from "react-bootstrap";
import ContactImg from "../Assets/images/contact-img.svg";

export const Contact = () => {
  const formInitialDetails = {
    firstName: "",
    lastName: "",
    email: "",
    phone: "",
    message: "",
  };

  const [formDetails, setFormDetails] = useState(formInitialDetails);
  const [buttonText, setButtonText] = useState("Send");
  const [status, setStatus] = useState({});

  const onFormUpdate = (category, value) => {
    setFormDetails({
      ...formDetails,
      [category]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setButtonText("Sending...");
    let response = await fetch("http://localhost:3000/contact", {
      method: "POST",
      headers: {
        "Content-Type": "application/json;charset=utf-8",
      },
      body: JSON.stringify(formDetails),
    });
    setButtonText("Envoyer");
    let result = await response.json();
    setFormDetails(formInitialDetails);
    if (result.code == 200) {
      setStatus({ succes: true, message: "Message envoyé avec succès." });
    } else {
      setStatus({
        succes: false,
        message: "Une erreur s'est produite. Merci de réessayer plus tard.",
      });
    }
  };

  return (
    <section className="contact" id="connect">
      <Container>
        <Row className="align-items-center">
          <Col md={6}>
            <img src={ContactImg} alt="Contact me" />
          </Col>
          <Col md={6}>
            <h2>Get In Touch</h2>
            <div>
              <p>
                <strong>Email:</strong> romain.le.neindre@gmail.com
              </p>
              <p>
                <strong>Téléphone:</strong> +33 6 10 42 56 27
              </p>
            </div>
          </Col>
        </Row>
      </Container>
    </section>
  );
};
